export default {
  name: 'ChatBox',
  data: () => ({
    // To keep the state of the input content
    text: ''
  }),
  methods: {
    // We will call this when the form is submitted
    onSubmit(event) {
      // This fires an event which we will handle
      // in the parent component
      this.$emit("submit", event, this.text);
      this.text = '';
    }
  }
};
