// UserInfo.vue

<template>
  <div v-if="user">
    Welcome {{ user.firstname }}!
  </div>

  <div v-else>
    Loading...
  </div>
</template>

<script>
import axios from 'axios';

export default {
  data() {
    return {
      user: null
    }
  },

  created() {
    const token = localStorage.getItem('token');

    if (token) {
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
    } else {
      console.log('Token not found');
    }

    axios.get('getUser')
      .then(response => {
        this.user = response.data.user; // Assuming user data is nested under 'user' key
      })
      .catch(error => {
        console.error(error);
      });
  }
}
</script>
