// UserInfo.vue

<template>
  <div v-if="user">
    Welcome {{ user.firstname }}!
  </div>

  <div v-else>
    Loading...
  </div>
</template>

<script>
import axios from 'axios';

export default {
  data() {
    return {
      user: null
    }
  },

  created() {
    axios.get('/getLoggedInUser')
      .then(response => {
        this.user = response.data
      })
      .catch(error => {
        console.log(error)  
      })
  }
}
</script>
