    Loading...
  </div>
</template>

<script>
import axios from 'axios';

export default {
  data() {
    return {
      user: null
    }
  },

  created() {
    axios.get('', {
  headers: {
    Authorization: `Bearer ${token}` 
  }
})
      .then(response => {
        this.user = response.data
      })
      .catch(error => {
        console.log(error)  
      })
  }
}
</script>
